meta.server:
  &lt;!-- Security listener.
Documentation at /docs/config/listeners.html
  &lt;Listener
className="org.apache.catalina.security.SecurityListener" /&gt;
  --&gt;
  &lt;!--APR library loader. Documentation at /docs/apr.html
--&gt;
  &lt;Listener
className="org.apache.catalina.core.AprLifecycleListener"
SSLEngine="on" /&gt;
  &lt;!--Initialize Jasper prior to webapps are loaded.
Documentation at /docs/jasper-howto.html --&gt;
  &lt;Listener
className="org.apache.catalina.core.JasperListener"
/&gt;
  &lt;!-- Prevent memory leaks due to use of particular
java/javax
APIs--&gt;
  &lt;Listener
className="org.apache.catalina.core.JreMemoryLeakPreventionListener"
/&gt;
  &lt;Listener
className="org.apache.catalina.mbeans.GlobalResourcesLifecycleListener"
/&gt;
  &lt;Listener
className="org.apache.catalina.core.ThreadLocalLeakPreventionListener"
/&gt;

  &lt;!-- Global JNDI resources
       Documentation at /docs/jndi-resources-howto.html
  --&gt;
  &lt;GlobalNamingResources&gt;
    &lt;!-- Editable user database that can also be used by
         UserDatabaseRealm to authenticate users
    --&gt;
    &lt;Resource name="UserDatabase" auth="Container"
              type="org.apache.catalina.UserDatabase"
              description="User database that can be updated and
saved"

factory="org.apache.catalina.users.MemoryUserDatabaseFactory"
              pathname="conf/tomcat-users.xml" /&gt;
  &lt;/GlobalNamingResources&gt;

  &lt;!-- A "Service" is a collection of one or more
"Connectors"
that share
       a single "Container" Note:  A "Service" is not itself a
"Container",
       so you may not define subcomponents such as "Valves" at this
level.
       Documentation at /docs/config/service.html
   --&gt;
  &lt;Service name="Catalina"&gt;

    &lt;!--The connectors can use a shared executor, you can
define
one or more named thread pools--&gt;
    &lt;!--
    &lt;Executor name="tomcatThreadPool"
namePrefix="catalina-exec-"
        maxThreads="150" minSpareThreads="4"/&gt;
    --&gt;


    &lt;!-- A "Connector" represents an endpoint by which
requests
are received
         and responses are returned. Documentation at :
         Java HTTP Connector: /docs/config/http.html (blocking
&amp; non-blocking)
         Java AJP  Connector: /docs/config/ajp.html
         APR (HTTP/AJP) Connector: /docs/apr.html
         Define a non-SSL HTTP/1.1 Connector on port 8080
    --&gt;
    &lt;Connector port="8080" protocol="HTTP/1.1"
               connectionTimeout="20000"
               redirectPort="8443"
               address="${env.OPENSHIFT_INTERNAL_IP}"
               URIEncoding="UTF-8"/&gt;
    &lt;!-- A "Connector" using the shared thread
pool--&gt;
    &lt;!--
    &lt;Connector executor="tomcatThreadPool"
               port="8080" protocol="HTTP/1.1"
               connectionTimeout="20000"
               redirectPort="8443" /&gt;
    --&gt;
    &lt;!-- Define a SSL HTTP/1.1 Connector on port 8443
         This connector uses the JSSE configuration, when using
APR, the
         connector should be using the OpenSSL style configuration
         described in the APR documentation --&gt;
    &lt;!--
    &lt;Connector port="8443" protocol="HTTP/1.1"
SSLEnabled="true"
               maxThreads="150" scheme="https" secure="true"
               clientAuth="false" sslProtocol="TLS" /&gt;
    --&gt;

    &lt;!-- Define an AJP 1.3 Connector on port 8009 --&gt;
    &lt;!--Connector port="8009" protocol="AJP/1.3"
redirectPort="8443" /--&gt;


    &lt;!-- An Engine represents the entry point (within
Catalina)
that processes
         every request.  The Engine implementation for Tomcat stand
alone
         analyzes the HTTP headers included with the request, and
passes them
         on to the appropriate Host (virtual host).
         Documentation at /docs/config/engine.html --&gt;

    &lt;!-- You should set jvmRoute to support load-balancing
via
AJP ie :
    &lt;Engine name="Catalina" defaultHost="localhost"
jvmRoute="jvm1"&gt;
    --&gt;
    &lt;Engine name="Catalina" defaultHost="localhost"&gt;

      &lt;!--For clustering, please take a look at
documentation
at:
          /docs/cluster-howto.html  (simple how to)
          /docs/config/cluster.html (reference documentation)
--&gt;
      &lt;!--
      &lt;Cluster
className="org.apache.catalina.ha.tcp.SimpleTcpCluster"/&gt;
      --&gt;

      &lt;!-- Use the LockOutRealm to prevent attempts to guess
user passwords
           via a brute-force attack --&gt;
      &lt;Realm
className="org.apache.catalina.realm.LockOutRealm"&gt;
        &lt;!-- This Realm uses the UserDatabase configured in
the
global JNDI
             resources under the key "UserDatabase".  Any edits
             that are performed against this UserDatabase are
immediately
             available for use by the Realm.  --&gt;
        &lt;Realm
className="org.apache.catalina.realm.UserDatabaseRealm"
               resourceName="UserDatabase"/&gt;
      &lt;/Realm&gt;

      &lt;Host name="localhost"  appBase="webapps"
            unpackWARs="false" autoDeploy="true"&gt;

        &lt;!-- SingleSignOn valve, share authentication
between
web applications
             Documentation at: /docs/config/valve.html --&gt;
        &lt;!--
        &lt;Valve
className="org.apache.catalina.authenticator.SingleSignOn"
/&gt;
        --&gt;

        &lt;!-- Access log processes all example.
             Documentation at: /docs/config/valve.html
             Note: The pattern used is equivalent to using
pattern="common" --&gt;
        &lt;Valve
className="org.apache.catalina.valves.AccessLogValve"
directory="logs"
               prefix="localhost_access_log." suffix=".txt"
               pattern="%h %l %u %t &amp;quot;%r&amp;quot;
%s %b"
/&gt;

      &lt;/Host&gt;
    &lt;/Engine&gt;
  &lt;/Service&gt;
